
$(function(){'use strict';$('.parent-menu').click(function(){if($(this).next('.sub-menu').hasClass('hide')){$(this).next('.sub-menu').removeClass('hide');$(this).children('.ionic-right').removeClass('glyphicon-menu-right');$(this).children('.ionic-right').addClass('glyphicon-menu-down');}else{$(this).next('.sub-menu').addClass('hide');$(this).children('.ionic-right').addClass('glyphicon-menu-right');$(this).children('.ionic-right').removeClass('glyphicon-menu-down');}});});